CREATE OR REPLACE FUNCTION public.generate_so_number()
 RETURNS text
 LANGUAGE plpgsql
AS $function$
DECLARE
    new_so_number TEXT;
    year_short TEXT;
    next_val BIGINT;
BEGIN
    -- Mengambil 2 digit terakhir dari tahun saat ini
    year_short := to_char(CURRENT_DATE, 'YY');
    
    -- Mengambil nomor urut berikutnya dari sequence
    next_val := nextval('public.order_number_seq');
    
    -- Menggabungkan format nomor
    new_so_number := 'BJS/SO/' || year_short || '/' || lpad(next_val::text, 5, '0');
    
    RETURN new_so_number;
END;
$function$

CREATE OR REPLACE FUNCTION public.hex_to_rgb(hex text)
 RETURNS double precision[]
 LANGUAGE plpgsql
 IMMUTABLE
AS $function$
DECLARE
    r int;
    g int;
    b int;
BEGIN
    r := ('x' || substring(hex, 2, 2))::bit(8)::int;
    g := ('x' || substring(hex, 4, 2))::bit(8)::int;
    b := ('x' || substring(hex, 6, 2))::bit(8)::int;
    RETURN ARRAY[r::double precision, g::double precision, b::double precision];
END;
$function$

CREATE OR REPLACE FUNCTION public.handle_updated_at()
 RETURNS trigger
 LANGUAGE plpgsql
AS $function$
BEGIN
  NEW.updated_at = NOW();
  RETURN NEW;
END;
$function$

CREATE OR REPLACE FUNCTION public.process_po_receipt(p_po_id uuid, p_invoice_image_url text, p_items jsonb)
 RETURNS void
 LANGUAGE plpgsql
 SECURITY DEFINER
AS $function$
DECLARE
    item_record jsonb;
    v_product_id uuid;
    v_quantity integer;
    v_new_purchase_price numeric;
    v_new_selling_price numeric;
    v_total_amount numeric := 0;
    v_supplier_name text;
    v_po_number text;
BEGIN
    PERFORM set_config('request.bypass_rls', 'on', true);

    FOR item_record IN SELECT * FROM jsonb_array_elements(p_items)
    LOOP
        v_product_id := (item_record->>'product_id')::uuid;
        v_quantity := (item_record->>'quantity_ordered')::integer;
        v_new_purchase_price := (item_record->>'new_purchase_price')::numeric;
        v_new_selling_price := (item_record->>'new_selling_price')::numeric;

        UPDATE public.purchase_order_items SET purchase_price = v_new_purchase_price, quantity_received = v_quantity WHERE purchase_order_id = p_po_id AND product_id = v_product_id;
        UPDATE public.products SET stok = stok + v_quantity, harga_beli = v_new_purchase_price, harga_jual = COALESCE(v_new_selling_price, harga_jual) WHERE id = v_product_id;
        INSERT INTO public.stock_logs(product_id, perubahan, keterangan) VALUES (v_product_id, v_quantity, 'Penerimaan barang dari PO');
        v_total_amount := v_total_amount + (v_new_purchase_price * v_quantity);
    END LOOP;

    SELECT po.po_number, s.nama_supplier INTO v_po_number, v_supplier_name FROM public.purchase_orders po LEFT JOIN public.suppliers s ON po.supplier_id = s.id WHERE po.id = p_po_id;
    UPDATE public.purchase_orders SET status = 'Selesai', total_amount = v_total_amount, invoice_image_url = p_invoice_image_url WHERE id = p_po_id;

    -- PERBAIKAN FINAL DI SINI: Menggunakan `kategori_pengeluaran`
    INSERT INTO public.expenses(tanggal, kategori_pengeluaran, keterangan, jumlah) 
    VALUES (CURRENT_DATE, 'Pembelian Stok', 'Pembelian ke ' || COALESCE(v_supplier_name, 'Supplier Umum') || ' (PO: ' || v_po_number || ')', v_total_amount);
END;
$function$

CREATE OR REPLACE FUNCTION public.log_product_changes()
 RETURNS trigger
 LANGUAGE plpgsql
AS $function$
BEGIN
    -- Jika ini adalah penambahan produk baru (INSERT)
    IF (TG_OP = 'INSERT') THEN
        INSERT INTO public.product_history_logs(product_id, change_type)
        VALUES(NEW.id, 'PRODUK DIBUAT');
        RETURN NEW;
    END IF;

    -- Jika ini adalah pengeditan produk (UPDATE)
    IF (TG_OP = 'UPDATE') THEN
        -- Cek dan catat perubahan NAMA PRODUK
        IF OLD.nama IS DISTINCT FROM NEW.nama THEN
            INSERT INTO public.product_history_logs(product_id, field_changed, old_value, new_value, change_type)
            VALUES(NEW.id, 'nama', OLD.nama, NEW.nama, 'EDIT PRODUK');
        END IF;

        -- Cek dan catat perubahan HARGA JUAL
        IF OLD.harga_jual IS DISTINCT FROM NEW.harga_jual THEN
            INSERT INTO public.product_history_logs(product_id, field_changed, old_value, new_value, change_type)
            VALUES(NEW.id, 'harga_jual', OLD.harga_jual::text, NEW.harga_jual::text, 'EDIT PRODUK');
        END IF;

        -- Cek dan catat perubahan HARGA BELI
        IF OLD.harga_beli IS DISTINCT FROM NEW.harga_beli THEN
            INSERT INTO public.product_history_logs(product_id, field_changed, old_value, new_value, change_type)
            VALUES(NEW.id, 'harga_beli', OLD.harga_beli::text, NEW.harga_beli::text, 'EDIT PRODUK');
        END IF;

        -- Cek dan catat perubahan STOK MINIMAL
        IF OLD.stok_min IS DISTINCT FROM NEW.stok_min THEN
            INSERT INTO public.product_history_logs(product_id, field_changed, old_value, new_value, change_type)
            VALUES(NEW.id, 'stok_min', OLD.stok_min::text, NEW.stok_min::text, 'EDIT PRODUK');
        END IF;

        -- ### TAMBAHAN BARU: Cek dan catat perubahan STOK ###
        IF OLD.stok IS DISTINCT FROM NEW.stok THEN
            INSERT INTO public.product_history_logs(product_id, field_changed, old_value, new_value, change_type)
            VALUES(NEW.id, 'stok', OLD.stok::text, NEW.stok::text, 'EDIT STOK MANUAL');
        END IF;

        RETURN NEW;
    END IF;

    RETURN NULL;
END;
$function$

CREATE OR REPLACE FUNCTION public.generate_invoice_number()
 RETURNS text
 LANGUAGE plpgsql
AS $function$
DECLARE
    new_inv_number TEXT;
    year_short TEXT;
    next_val BIGINT;
BEGIN
    -- Menggunakan sequence yang sama dengan SO agar nomor urutnya sinkron
    next_val := nextval('public.order_number_seq');
    year_short := to_char(CURRENT_DATE, 'YY');
    new_inv_number := 'BJS/INV/' || year_short || '/' || lpad(next_val::text, 5, '0');
    RETURN new_inv_number;
END;
$function$

CREATE OR REPLACE FUNCTION public.process_grosir_invoice(p_sales_order_id uuid, p_customer_id uuid, p_invoice_number text, p_termin_pembayaran text, p_items jsonb)
 RETURNS uuid
 LANGUAGE plpgsql
AS $function$
DECLARE
    -- ... (deklarasi variabel tidak berubah) ...
    new_invoice_id uuid;
    total_subtotal numeric := 0;
    total_diskon_item numeric := 0;
    total_laba_invoice numeric := 0;
    item record;
    product_info record;
BEGIN
    -- ... (blok kalkulasi total tidak berubah) ...
    FOR item IN SELECT * FROM jsonb_to_recordset(p_items) AS x(product_id uuid, kuantitas integer, harga_grosir_deal numeric, diskon_item_rp numeric) LOOP
        total_subtotal := total_subtotal + (item.harga_grosir_deal * item.kuantitas);
        total_diskon_item := total_diskon_item + (item.diskon_item_rp * item.kuantitas);
    END LOOP;

    -- ... (blok insert ke tabel invoices tidak berubah) ...
    INSERT INTO public.invoices(sales_order_id, customer_id, invoice_number, termin_pembayaran, subtotal, total_diskon, total_akhir, status_pembayaran)
    VALUES (p_sales_order_id, p_customer_id, p_invoice_number, p_termin_pembayaran, total_subtotal, total_diskon_item, total_subtotal - total_diskon_item, 'Belum Lunas')
    RETURNING id INTO new_invoice_id;

    -- ... (blok loop untuk menyimpan item tidak berubah) ...
     FOR item IN SELECT * FROM jsonb_to_recordset(p_items) AS x(product_id uuid, kuantitas integer, harga_grosir_deal numeric, diskon_item_rp numeric) LOOP
        INSERT INTO public.invoice_items(invoice_id, product_id, kuantitas, harga_grosir_deal, diskon_item_rp, subtotal)
        VALUES (new_invoice_id, item.product_id, item.kuantitas, item.harga_grosir_deal, item.diskon_item_rp, (item.harga_grosir_deal * item.kuantitas));
        SELECT harga_beli INTO product_info FROM public.products WHERE id = item.product_id;
        total_laba_invoice := total_laba_invoice + ((item.harga_grosir_deal * item.kuantitas) - (product_info.harga_beli * item.kuantitas) - (item.diskon_item_rp * item.kuantitas));
        UPDATE public.products SET stok = stok - item.kuantitas, stok_dialokasikan = stok_dialokasikan - item.kuantitas WHERE id = item.product_id;
    END LOOP;

    -- --- PERUBAHAN DI SINI ---
    -- 4. Buat record di tabel transactions, sekarang dengan invoice_number
    INSERT INTO public.transactions(customer_id, total, diskon, total_akhir, bayar, kembalian, total_laba, items, status_pembayaran, sisa_hutang, invoice_number)
    VALUES(p_customer_id, total_subtotal, total_diskon_item, total_subtotal - total_diskon_item, 0, 0, total_laba_invoice, p_items, 'Belum Lunas', total_subtotal - total_diskon_item, p_invoice_number); -- <-- Tambahkan p_invoice_number

    -- Baris untuk update sales_orders sudah kita hapus sebelumnya (ini sudah benar)

    RETURN new_invoice_id;
END;
$function$
